import portrait from "../assets/images/portrait.png"
import "./About.css"

export function About() {
  // const containerStyle = {
  //   padding: "20px",
  //   marginTop: "30px",
  //   marginBottom: "50px",
  //   backgroundColor: "black",
  //   color: "white",
  //   fontSize: "20px",
  //   opacity: "0.9"
  // }

  // const portraitStyle = {
  //   height: "200px",
  //   width: "200px",
  //   border: "2px solid white",
  //   borderRadius: "50%",
  //   margin: "0px 20px 60px 20px"
  // }

  // const cardStyle = {
  //   backgroundColor: "inherit"
  // }

  // const buttonStyle = {
  //   backgroundColor: "rgb(242, 130, 78)",
  //   fontSize: "25px",
  //   color: "white",
  //   marginRight: "30px",
  //   fontFamily: "Arial"
  // }

  return (
    <section className="container" id="aboutContainer" style={containerStyle}>
      <div className="row justify-content-center" id="aboutRow">
        <div className="col-sm-12 col-md-3">
          <img src={portrait} alt="portrait" id="portrait" style={portraitStyle}/>
        </div>
      </div>
      <div className="row" id="bioRow">
        <div className="col-sm-12">
          <h1 className="card-title" style={{textAlign: "center"}}>
            About Me
          </h1>

          <div className="card w-100" id="bioCard" style={cardStyle}>
            <div className="card-body">
              <br />
              <h3>Introduction</h3>
              <br />
              <p>My name is Fahad Hai, and I am a Full Stack Web Developer and UX Designer based in Houston, Texas.</p>
              <br />
              <h3>Design and Programming Philosophy</h3>
              <br />
              <p>I follow the three "C"s: "Clean,
                Concise, Connected", or a modern interface requiring minimal interaction to achieve purpose while
                providing an enjoyable
                experience to the user. <u>"If it can be done with a single click, why use two?"</u></p>
              <br />
              <h3>Career goals and interests</h3>
              <br />
              <p>I chose web development and UX Design as my career because along with being a 'techie' I have
                always wanted to design and create content rather than just consume it. I am a firm believer that these
                two disciplines complement each other extremely well as they enable me to build content from the concept
                stage all to the finished product. Rice University and Google helped me realize my potential
                and now I welcome new challenges and opportunities to expand my horizons. </p>
              <br />
              <h3>Education</h3>
              <br />
              <ul>
                <li>
                  <p><b><u>Rice University Full Stack Web Development Bootcamp:</u></b> An intensive program
                    focused on
                    gaining
                    technical programming skills in HTML5, CSS3, Javascript, JQuery, APIs, Bootstrap, Firebase, Node Js,
                    MySQL, MongoDB, Express, Handlebars.js & ReactJS.</p>
                </li>
                <li>
                  <p><b><u>Google UX Design Certification:</u></b> </p>
                </li>
              </ul>
              <br />
              <h3>Background</h3>
              <br />
              <p>I was born in Karachi, Pakistan, and I'm
                a US citizen and a resident of the beautiful city of Houston, Texas. I have a Bachelors degree
                in Medicine from Pakistan, but after immigrating I decided to change career paths. I worked for 9 years as
                a administrative assistant and lead concierge at Northshore Skokie Hospital in Chicago before moving to
                Texas in 2019.</p>
              <br />
            </div>
            <div className="card-footer" style={{textAlign: "center"}}>
              <a href="https://twitter.com/wdfhai" id="twitterBtn" target="_blank" className="btn btn-lg" style={buttonStyle} title="Twitter"><i
                  className="fab fa-twitter"></i></a>
              <a href="https://github.com/wdfhai" id="gitHubBtn" target="_blank" className="btn btn-lg" style={buttonStyle} title="GitHub"><i
                  className="fab fa-github"></i></a>
              <a href="https://www.linkedin.com/in/fahadhai/" id="linkedInBtn" target="_blank" className="btn btn-lg" style={buttonStyle}
                title="LinkedIn"><i className="fab fa-linkedin"></i></a>
              <a href="https://docs.google.com/document/d/1fOminBXUXq6uDrt6WL-xqS93pafk3wpflrxIUtuSx2k/edit?usp=sharing"
                id="cvBtn" target="_blank" className="btn btn-lg" style={buttonStyle} title="CV">CV</a>
              <a href="mailto: wd.fhai@gmail.com" target="_blank" id="emailBtn" className="btn btn-lg" style={buttonStyle} title="Email"><i
                  className="fas fa-envelope-square"></i> at wd.fhai@outlook.com</a>
              <a href="tel:+1-224-830-3565" target="_blank" id="phoneBtn" className="btn btn-lg" style={buttonStyle} title="Phone"><i
                  className="fas fa-phone"></i> at 224-830-3565</a>
            </div>
          </div>
        </div>
      </div>
    </section>
  )
}
